import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import "./ReferralApp.css";
import { useAuth0 } from '@auth0/auth0-react';

const baseUrl = 'http://localhost:8000';

const ReferralApp: React.FC = () => {
  const [referralTo, setReferralTo] = useState('');
  const [reasonForReferral, setReasonForReferral] = useState('');
  const [notes, setNotes] = useState('');
  const [referralResponse, setReferralResponse] = useState(''); // State to hold the editable referral response

  const { getAccessTokenSilently, logout } = useAuth0();

  const handleGenerateReferral = async () => {
    const formattedNotes = `Referral to: ${referralTo}\nReason for referral: ${reasonForReferral}\n\n${notes}`;
    const payload = { content: formattedNotes };

    try {
      const token = await getAccessTokenSilently();
      const response = await fetch(`${baseUrl}/generate-referral/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      if (data.message) {
        setReferralResponse(data.message); // Set the editable referral response
      } else {
        setReferralResponse(`Run Status: ${data.status}`);
      }
    } catch (error) {
      if (error instanceof Error) {
        console.error(`Failed to generate referral: ${error.message}`);
        setReferralResponse(`Error: ${error.message}`);
      }
    }
  };

  return (
    <div className="App">
      <div className="App-header">
        <h1>Copy-Paste-Refer</h1>
        <LogoutButton logout={logout} />
        <textarea
          className="ReferralTo"
          value={referralTo}
          onChange={(e) => setReferralTo(e.target.value)}
          placeholder="Referral to"
          rows={1}
          cols={40}
        />
        <textarea
          className="ReasonForReferral"
          value={reasonForReferral}
          onChange={(e) => setReasonForReferral(e.target.value)}
          placeholder="Reason for referral"
          rows={1}
          cols={40}
        />
        <textarea
          className="Notes-input textarea-border"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          placeholder="Enter patient notes here"
          rows={10}
          cols={100}
        />
        <button className="GenerateButton" onClick={handleGenerateReferral}>Generate Referral</button>
        <h2>Referral Letter:</h2>
        <div className="ql-editor">
          <ReactQuill 
            theme="snow"
            value={referralResponse}
            onChange={setReferralResponse}
            readOnly={false}
            modules={{ toolbar: true }}
          />
        </div>
        <button className="SendToSecretaryButton">Send to Secretary</button>
      </div>
    </div>
  );
};

const LogoutButton: React.FC<{ logout: ReturnType<typeof useAuth0>['logout'] }> = ({ logout }) => (
  <button onClick={() => logout({ logoutParams: { returnTo: window.location.origin } })}>Log Out</button>
);

export default ReferralApp;
