import React, { useState } from 'react';
import './App.css';
import { createSession, sendMessage } from './controllers/ReferralController'; // Import the API functions

function App() {
  const [notes, setNotes] = useState<string>('');
  const [letter, setLetter] = useState<string>('');
  const [sessionId, setSessionId] = useState<string | null>(null); // Store the session ID

  const handleNotesChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNotes(event.target.value);
  };

  const handleGenerateLetter = async () => {
    try {
      if (!sessionId) {
        const newSessionId = await createSession(); // Create a new session if none exists
        setSessionId(newSessionId);
      }
      const generatedLetter = await sendMessage(sessionId!, notes); // Send the notes as a message
      setLetter(generatedLetter); // Set the generated letter received from the API
    } catch (error) {
      console.error("Failed to generate letter:", error);
      alert("There was an error generating the letter. Please try again.");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Referral Letter Generator</h1>
        <textarea
          value={notes}
          onChange={handleNotesChange}
          placeholder="Enter patient notes here..."
          rows={10}
          cols={100}
        />
        <button onClick={handleGenerateLetter}>Generate Letter</button>
        {letter && (
          <div>
            <h2>Referral Letter:</h2>
            <p>{letter}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
