// src/api/chatApi.ts
const baseUrl = 'http://localhost:8000'; 

export const startReferral = async (message) => {
    const response = await fetch(`${baseUrl}/start-referral/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
    });
    return response;  // Make sure to return the response directly, not response.json()
};
