import React, { useState } from 'react';

const baseUrl = 'http://localhost:8000';

function App() {
    const [notes, setNotes] = useState('');
    const [responses, setResponses] = useState<string[]>([]); // Store messages or status updates

    const handleGenerateReferral = async () => {
        const payload = {
            content: notes
        };

        try {
            const response = await fetch(`${baseUrl}/generate-referral/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(payload)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            if (data.message) {
                setResponses([data.message]);
            } else {
                setResponses([`Run Status: ${data.status}`]);
            }
        } catch (error) {
            if (error instanceof Error) {
                console.error(`Failed to generate referral: ${error.message}`);
                setResponses([`Error: ${error.message}`]);
            }
        }
    };

    return (
        <div className="App">
            <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Enter patient notes here"
                rows={10}
                cols={50} // Adjusted for better layout
            />
            <button onClick={handleGenerateReferral}>Generate Referral</button>
            <div>
                <h2>Referral Response:</h2>
                {responses.map((response, index) => (
                    <pre key={index}>{response}</pre>
                ))}
            </div>
        </div>
    );
}

export default App;
