import React, { useState } from 'react';

const baseUrl = 'http://localhost:8000';

function App() {
    const [notes, setNotes] = useState('');
    const [responses, setResponses] = useState('');

    const handleGenerateReferral = async () => {
        const payload = {
            content: notes
        };

        try {
            const response = await fetch(`${baseUrl}/generate-referral/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(payload)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            setResponses(`Thread and message successfully processed. Thread ID: ${data.thread_id}, Message ID: ${data.message_id}`);
        } catch (error) {
            setResponses(`Failed to generate referral: ${error.message}`);
        }
    };

    return (
        <div className="App">
            <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Enter patient notes here"
                rows={10}
                cols={100}
            />
            <button onClick={handleGenerateReferral}>Generate Referral</button>
            <div>
                <h2>Referral Response:</h2>
                <pre>{responses}</pre>
            </div>
        </div>
    );
}

export default App;
