import React, { useState } from 'react';
import { startReferral } from './api/chatApi';

const baseUrl = 'http://localhost:8000';

function App() {
    const [notes, setNotes] = useState('');
    const [responses, setResponses] = useState('');

    const handleGenerateReferral = async () => {
        try {
            const response = await startReferral(notes);
            const data = await response.json(); // Ensure this line correctly parses the JSON response
            const threadId = data.thread_id; // Make sure the key matches what's returned by the backend

            const eventSource = new EventSource(`${baseUrl}/stream-referral/${threadId}`);
            eventSource.onmessage = (event) => {
                setResponses(prev => `${prev}\n${event.data}`);
            };

            eventSource.onerror = (error) => {
                console.error('EventSource failed:', error);
                eventSource.close();
            };

            eventSource.addEventListener('done', () => {
                console.log('Stream completed');
                eventSource.close();
            });
        } catch (error) {
            console.error("Error generating referral:", error);
            alert("There was an error generating the referral. Please try again.");
        }
    };

    return (
        <div className="App">
            <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Enter patient notes here"
                rows={10}
                cols={100}
            />
            <button onClick={handleGenerateReferral}>Generate Referral</button>
            <div>
                <h2>Referral Response:</h2>
                <pre>{responses}</pre>
            </div>
        </div>
    );
}

export default App;
