// src/api/chatApi.ts
const baseUrl = 'http://localhost:8000'; 

export const startReferral = async (message: string) => {
    const response = await fetch(`${baseUrl}/start-referral/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
    });
    if (!response.ok) {
        throw new Error('Failed to start referral process');
    }
    const data = await response.json();
    console.log('Received data:', data);  // Make sure this logs the object you've shown
    if (!data || !data.thread_id) {
        console.error('Failed to receive valid thread ID:', data);
        alert('Failed to start the referral process. No thread ID received.');
        return;  // Prevent further execution if thread_id is undefined or invalid
    }
    console.log('Using thread ID:', data.thread_id);
    const eventSourceUrl = `${baseUrl}/stream-referral/${data.thread_id}`;
    console.log('Connecting to EventSource at:', eventSourceUrl);
    const eventSource = new EventSource(eventSourceUrl);
    
};
