import React, { useState } from 'react';
import './App.css'; // Assuming basic styling from Create React App

function App() {
  const [notes, setNotes] = useState<string>(''); // State to hold notes input
  const [letter, setLetter] = useState<string>(''); // State to hold the generated letter

  const handleNotesChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNotes(event.target.value); // Update state when text area input changes
  };

  const handleGenerateLetter = async () => {
    // Here you would put the fetch API call to the FastAPI backend
    // Simulating a response for now
    setLetter(`Generated Referral Letter based on the following notes: ${notes}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Referral Letter Generator</h1>
        <textarea
          value={notes}
          onChange={handleNotesChange}
          placeholder="Enter patient notes here..."
          rows={10}
          cols={50}
        />
        <button onClick={handleGenerateLetter}>Generate Letter</button>
        {letter && (
          <div>
            <h2>Referral Letter</h2>
            <p>{letter}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
