// src/api/chatApi.ts
const baseUrl = 'http://localhost:8000'; // Ensure this matches your FastAPI server URL

export const startReferral = async (message: string) => {
    const response = await fetch(`${baseUrl}/start-referral/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
    });
    if (!response.ok) {
        throw new Error('Failed to start referral process');
    }
    return response.json();
};

export const generateReferral = (message: string) => {
    return new EventSource(`${baseUrl}/generate-referral/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
    });
};
