import React, { useState } from 'react';
import { startReferral } from './api/chatApi';

const baseUrl = 'http://localhost:8000';

function App() {
    const [notes, setNotes] = useState('');
    const [responses, setResponses] = useState('');

    const handleGenerateReferral = async () => {
        try {
            const data = await startReferral(notes);
            if (!data || !data.thread_id) {
                console.error('Failed to receive valid thread ID:', data);
                alert('Failed to start the referral process. No thread ID received.');
                return;  // Prevent further execution if threadId is undefined or invalid
            }
            console.log('Using thread ID:', data.threadId);
            const eventSourceUrl = `${baseUrl}/stream-referral/${data.threadId}`;
            console.log('Connecting to EventSource at:', eventSourceUrl);
            const eventSource = new EventSource(eventSourceUrl);

            eventSource.onmessage = (event) => {
                console.log('Event Received:', event.data);
                if (event.data.includes('Error')) {
                    console.error('Stream Error:', event.data);
                    eventSource.close(); // Consider closing EventSource on critical errors
                } else {
                    setResponses(prev => `${prev}\n${event.data}`);
                }
            };

            eventSource.onerror = (error) => {
                console.error('EventSource failed:', error);
                eventSource.close();
            };

            eventSource.addEventListener('done', () => {
                console.log('Stream completed');
                eventSource.close();
            });
        } catch (error) {
            console.error("Error generating referral:", error);
            alert("There was an error generating the referral. Please try again.");
        }
    };

    return (
        <div className="App">
            <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Enter patient notes here"
                rows={10}
                cols={100}
            />
            <button onClick={handleGenerateReferral}>Generate Referral</button>
            <div>
                <h2>Referral Response:</h2>
                <pre>{responses}</pre>
            </div>
        </div>
    );
}

export default App;
