import React, { useState, useEffect } from 'react';

const baseUrl = 'http://localhost:8000';

function App() {
    const [notes, setNotes] = useState('');
    const [responses, setResponses] = useState<string[]>([]);

    const handleGenerateReferral = async () => {
        const payload = {
            content: notes
        };

        try {
            const response = await fetch(`${baseUrl}/generate-referral/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(payload)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json();
            console.log('Referral generation started:', data);
        } catch (error) {
            if (error instanceof Error) {
                console.error(`Failed to generate referral: ${error.message}`);
            }
        }
    };

    return (
        <div className="App">
            <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Enter patient notes here"
                rows={10}
                cols={100}
            />
            <button onClick={handleGenerateReferral}>Generate Referral</button>
            <div>
                <h2>Referral Responses:</h2>
                {responses.map((response, index) => (
                    <pre key={index}>{response}</pre>
                ))}
            </div>
        </div>
    );
}

export default App;
